import {useState} from 'react';
import './App.css';

function App() {
  const [userName, setUserName] = useState('');
  const [password, setPassword] = useState('');

  const formNames = {
    userName: 'username',
    password: 'password'
  }

  const handleOnChange = (e) => {
      const name = e.target.name
      if(name === formNames.userName) {
        setUserName(e.target.value)
      } 
      else if (name === formNames.password) {
        setPassword(e.target.value)
      }
  }

  const handleLogin = () => {
      if(userName.length && password.length) {
        console.log('you are logged in')
      }
  }

  return (
    <div className="login">
      {/* logo */}
      <div className='login__logo'>
        logo
      </div>
      {/* username */}
      <div className='login__userName'>
        <label htmlFor="username">Username</label>
        <input type="text" name="username" id="username" value={userName} onChange={(e)=> handleOnChange(e)}   />
      </div>
      {/* password */}
      <div className='login__password'>
        <label htmlFor="password">Password</label>
        <input type="password" name="password" id="password" value={password} onChange={(e)=> handleOnChange(e)} />
      </div>

      {/* button */}
      <div className='login__login'>
        <button onClick={() => handleLogin()}>login</button>
      </div>
    </div>
  );
}

export default App;
